import styled from "styled-components";

export const LOGOBGContainer = styled.div`
  width: 40px;
  height: 40px;
  border: 3px solid #0cc9c9;
  transform: rotate(45deg);
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
`;

export const ContentContainer = styled.div`
  transform: rotate(-45deg);
  font-size: 20px;
  color: #0cc9c9;
`;

export const DiamondContainerTop = styled.div`
  width: 4px;
  height: 4px;
  background-color: #0cc9c9;
  position: absolute;
  top: 5px;
  left: 5px;
`;

export const DiamondContainerBottom = styled.div`
  width: 4px;
  height: 4px;
  background-color: #0cc9c9;
  position: absolute;
  bottom: 5px;
  right: 5px;
`;

export const ShopTeamContentContainer = styled.span`
  position: absolute;
  top: 8px;
  left: 18px;
  font-size: 22px;
  color: #0cc9c9;
  background-color: #fff;
  font-family: "Teko", sans-serif;
`;
