import { Link } from "react-router-dom";
import styled from "styled-components";
import {
  FaBars,
  FaSearch,
  FaShoppingBasket,
  FaUserCircle,
} from "react-icons/fa";
import Logo from "../Logo/index";
const color = "#0cc9c9";

export const HeaderContainer = styled.header`
  width: 100%;
  height: 100%;
  padding: 17px 0px;
  box-shadow: 0 2px 5px 0 rgba(175, 175, 175, 0.3);
`;

export const NavContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: flex-start;
  background-color: red;

  /* Responsive*/
  @media only screen and (min-width: 1200px) {
    margin-left: 5%;
    margin-right: 5%;
  }
  @media only screen and (min-width: 900px) and (max-width: 1200px) {
    margin-left: 3%;
    margin-right: 3%;
  }
  @media only screen and (min-width: 600px) and (max-width: 900px) {
    display: block;
  }
`;

const LOGOContainer = styled.div`
  width: 125px;
  height: 100%;
`;

const MenuBarContainer = styled.div`
  width: 90%;
  margin-left: 5%;
`;

export const ULElement = styled.ul`
  margin: 0;
  padding: 0;
  display: flex;
  list-style-type: none;
`;

export const LIElement = styled.li`
  text-decoration: none;
  font-size: 16px;
  flex-grow: 1;
`;

export const LinkContainer = styled(Link)`
  text-decoration: none;
  font-weight: bold;
  font-family: "Fira Sans", sans-serif;
  color: ${(props) => (props.primary ? color : "black")};
  display: inline-block;
  &:hover {
    color: ${color};
  }

  &::after {
    content: "";
    width: 0px;
    height: 1px;
    display: block;
    background: ${color};
    transition: 300ms;
  }
  &:hover::after {
    width: 100%;
  }
`;

export const Menubar = function () {
  return (
    <NavContainer>
      <LOGOContainer>
        <Logo />
      </LOGOContainer>
      <MenuBarContainer>
        <ULElement>
          <LIElement>
            <LinkContainer primary to="admin/user">
              Home
            </LinkContainer>
          </LIElement>
          <LIElement>
            <LinkContainer to="">Shop</LinkContainer>
          </LIElement>
          <LIElement>
            <LinkContainer to="">Blog</LinkContainer>
          </LIElement>
          {/* <LIElement>
            <LinkContainer to="">Contact</LinkContainer>
          </LIElement> */}
          <LIElement>
            <LinkContainer to="">Seller Channel</LinkContainer>
          </LIElement>
          <LIElement padding width>
            <LinkContainer to="">
              <FaUserCircle />
            </LinkContainer>
          </LIElement>
          <LIElement>
            <LinkContainer to="">
              <FaShoppingBasket />
            </LinkContainer>
          </LIElement>
          <LIElement>
            <LinkContainer to="">
              <FaSearch />
            </LinkContainer>
          </LIElement>
          <LIElement paddingright>
            <LinkContainer to="">
              <FaBars />
            </LinkContainer>
          </LIElement>
        </ULElement>
      </MenuBarContainer>
    </NavContainer>
  );
};
