import { Link } from "react-router-dom";
import styled from "styled-components";
import {
  FaBars,
  FaSearch,
  FaShoppingBasket,
  FaUserCircle,
} from "react-icons/fa";
import Logo from "../Logo/index";
import { useState } from "react";
const color = "#0cc9c9";

export const HeaderContainer = styled.header`
  width: 100%;
  height: 100%;
  padding: 17px 0px;
  box-shadow: 0 2px 5px 0 rgba(175, 175, 175, 0.3);
`;

export const NavContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: flex-start;
  background-color: red;

  /* Responsive*/
  @media only screen and (min-width: 1200px) {
    margin-left: 5%;
    margin-right: 5%;
  }
  @media only screen and (min-width: 900px) and (max-width: 1200px) {
    margin-left: 3%;
    margin-right: 3%;
  }
  @media only screen and (max-width: 900px) {
    margin-left: 1%;
    margin-right: 1%;
  }
`;

const LOGOContainer = styled.div`
  width: 125px;
  background-color: black;
  height: 100%;
  @media only screen and (max-width: 900px) {
    margin-left: 3%;
  }
`;

const MenuBarContainer = styled.div`
  width: 90%;
  margin-left: 5%;
  @media only screen and (max-width: 900px) {
    position: relative;
    display: flex;
    justify-content: flex-end;
    font-size: 25px;
    padding-right: 10px;
  }
`;

const ULElement = styled.ul`
  margin: 0;
  padding: 0;
  display: flex;
  list-style-type: none;
  @media only screen and (max-width: 900px) {
    display: none;
  }
`;

const LIElement = styled.li`
  text-decoration: none;
  font-size: 16px;
  flex-grow: 1;
`;

const LinkContainer = styled(Link)`
  text-decoration: none;
  font-weight: bold;
  font-family: "Fira Sans", sans-serif;
  color: ${(props) => (props.primary ? "#0cc9c9" : "black")};
  display: inline-block;
  &:hover {
    color: ${color};
  }

  &::after {
    content: "";
    width: 0px;
    height: 1px;
    display: block;
    background: ${color};
    transition: 300ms;
  }
  &:hover::after {
    width: 100%;
  }
`;

const FaBarsConatiner = styled.div`
  @media only screen and (min-width: 900px) {
    display: none;
  }
  @media only screen and (max-width: 900px) {
    display: block;
  }
`;

export const OpacityContainer = styled.div`
  @media only screen and (min-width: 900px) {
    display: none;
  }
  @media only screen and (max-width: 900px) {
    display: block;
    position: absolute;
    background-color: black;
    width: 50%;
    height: 100%;
    opacity: 0.3;
    top: 0;
  }
`;

export const Menubar = function () {
  const [checkedMenu, setCheckedMenu] = useState(false);

  function handlerOnclickForMenu() {
    setCheckedMenu(!checkedMenu);
  }
  console.log(checkedMenu);
  return (
    <NavContainer>
      <LOGOContainer>
        <Logo />
      </LOGOContainer>
      <MenuBarContainer>
        <ULElement>
          <LIElement>
            <LinkContainer primary="true" to="admin/user">
              Home
            </LinkContainer>
          </LIElement>
          <LIElement>
            <LinkContainer to="">Shop</LinkContainer>
          </LIElement>
          <LIElement>
            <LinkContainer to="">Blog</LinkContainer>
          </LIElement>
          <LIElement>
            <LinkContainer to="">Contact</LinkContainer>
          </LIElement>
          <LIElement>
            <LinkContainer to="">Seller Channel</LinkContainer>
          </LIElement>
          <LIElement>
            <LinkContainer to="">
              <FaUserCircle />
            </LinkContainer>
          </LIElement>
          <LIElement>
            <LinkContainer to="">
              <FaShoppingBasket />
            </LinkContainer>
          </LIElement>
          <LIElement>
            <LinkContainer to="">
              <FaSearch />
            </LinkContainer>
          </LIElement>
        </ULElement>
        <FaBarsConatiner onClick={() => handlerOnclickForMenu()}>
          <FaBars />
        </FaBarsConatiner>
      </MenuBarContainer>
    </NavContainer>
  );
};
