import styled from "styled-components";

export const LOGOBGContainer = styled.div`
  width: 40px;
  height: 40px;
  border: 3px solid #0cc9c9;
  transform: rotate(45deg);
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
`;

export const ContentContainer = styled.div`
  transform: rotate(-45deg);
  font-size: 20px;
  color: #0cc9c9;
`;

export const DiamondContainerTop = styled.div`
  width: 4px;
  height: 4px;
  background-color: #0cc9c9;
  position: absolute;
  top: 3px;
  left: 3px;
`;

export const DiamondContainerLeft = styled.div`
  width: 6px;
  height: 6px;
  background-color: #0cc9c9;
  position: absolute;
  bottom: 3px;
  left: 3px;
`;

export const ShopTeamContentContainer = styled.div`
  position: absolute;
  top: 10px;
  left: 20px;
  font-size: 22px;
  color: #0cc9c9;
  background-color: #fff;
  font-family: "Teko", sans-serif;
`;
