import { Link } from "react-router-dom";
import styled from "styled-components";
import {
  FaBars,
  FaSearch,
  FaShoppingBasket,
  FaUserCircle,
} from "react-icons/fa";
const color = "#0cc9c9";

export const ULElement = styled.ul`
  margin: 0;
  padding: 0;
  width: 100%;
  display: flex;
  list-style-type: none;
  position: relative;
`;

export const LIElement = styled.li`
  text-decoration: none;
  @media only screen and (min-width: 1200px) {
    font-size: 16px;
    text-align: ${(props) => (props.width ? "center" : "")};
  }

  @media screen and (min-width: 1024px) and (max-width: 1200px) {
    font-size: 14px;
    text-align: ${(props) => (props.width ? "center" : "")};
    padding: ${(props) => (props.padding ? "0 10px" : "0 25px")};
  }

  @media screen and (min-width: 900px) and (max-width: 1023px) {
    font-size: 14px;
    text-align: ${(props) => (props.width ? "center" : "")};
    padding: ${(props) => (props.padding ? "0 20px" : "0 17px")};
  }
`;

export const LinkContainer = styled(Link)`
  text-decoration: none;
  font-weight: bold;
  font-family: "Fira Sans", sans-serif;
  color: ${(props) => (props.primary ? color : "black")};
  display: inline-block;
  &:hover {
    color: ${color};
  }

  &::after {
    content: "";
    width: 0px;
    height: 1px;
    display: block;
    background: ${color};
    transition: 300ms;
  }
  &:hover::after {
    width: 100%;
  }
`;

export const Menubar = function () {
  return (
    <ULElement>
      <LIElement>
        <LinkContainer primary to="admin/user">
          Home
        </LinkContainer>
      </LIElement>
      <LIElement>
        <LinkContainer to="">Shop</LinkContainer>
      </LIElement>
      <LIElement>
        <LinkContainer to="">Blog</LinkContainer>
      </LIElement>
      <LIElement>
        <LinkContainer to="">Contact</LinkContainer>
      </LIElement>
      <LIElement>
        <LinkContainer to="">Seller Channel</LinkContainer>
      </LIElement>
      <LIElement padding>
        <LinkContainer to="">
          <FaUserCircle />
        </LinkContainer>
      </LIElement>
      <LIElement>
        <LinkContainer to="">
          <FaShoppingBasket />
        </LinkContainer>
      </LIElement>
      <LIElement>
        <LinkContainer to="">
          <FaSearch />
        </LinkContainer>
      </LIElement>
      <LIElement>
        <LinkContainer to="">
          <FaBars />
        </LinkContainer>
      </LIElement>
    </ULElement>
  );
};
