import { Link } from "react-router-dom";
import styled from "styled-components";
import {
  FaBars,
  FaSearch,
  FaShoppingBasket,
  FaUserCircle,
} from "react-icons/fa";
const color = "#0cc9c9";

export const ULElement = styled.ul`
  margin: 0;
  padding: 0;
  display: flex;
  list-style-type: none;
  position: relative;
`;
const SmallULEmlemet = styled.ul`
  position: absolute;
  display: none;
  list-style-type: none;
  margin: 0;
  padding: 0;
`;

const SmallLIEmlemet = styled.li`
  padding: ${(props) => (props.padding ? "40px 0 10px 0 " : "10px 0")};
`;

export const LIElement = styled.li`
  text-decoration: none;
  padding: ${(props) => (props.padding ? "0 100px" : "0 40px")};
`;

export const LinkContainer = styled(Link)`
  text-decoration: none;
  font-size: 16px;
  font-weight: bold;
  font-family: "Fira Sans", sans-serif;
  color: ${(props) => (props.primary ? color : "black")};
  display: inline-block;
  &:hover ${SmallULEmlemet} {
    color: ${color};
    transition: 300ms;
    display: block;
  }

  &::after {
    content: "";
    width: 0px;
    height: 1px;
    display: block;
    background: ${color};
    transition: 300ms;
  }
  &:hover::after {
    width: 100%;
  }
`;

export const Menubar = function () {
  return (
    <ULElement>
      <LIElement>
        <LinkContainer primary to="admin/user">
          Home
        </LinkContainer>
      </LIElement>
      <LIElement>
        <LinkContainer to="">
          Pages
          <SmallULEmlemet>
            <SmallLIEmlemet padding>1</SmallLIEmlemet>
            <SmallLIEmlemet>2</SmallLIEmlemet>
            <SmallLIEmlemet>34</SmallLIEmlemet>
            <SmallLIEmlemet>4</SmallLIEmlemet>
          </SmallULEmlemet>
        </LinkContainer>
      </LIElement>
      <LIElement>
        <LinkContainer to="">
          Destinations
          <SmallULEmlemet>
            <SmallLIEmlemet padding>1</SmallLIEmlemet>
            <SmallLIEmlemet>2</SmallLIEmlemet>
            <SmallLIEmlemet>34</SmallLIEmlemet>
            <SmallLIEmlemet>4</SmallLIEmlemet>
          </SmallULEmlemet>
        </LinkContainer>
      </LIElement>
      <LIElement>
        <LinkContainer to="">Blog</LinkContainer>
      </LIElement>
      <LIElement>
        <LinkContainer to="">Shop</LinkContainer>
      </LIElement>
      <LIElement>
        <LinkContainer to="">Element</LinkContainer>
      </LIElement>
      <LIElement padding>
        <LinkContainer to="">
          <FaUserCircle />
        </LinkContainer>
      </LIElement>
      <LIElement>
        <LinkContainer to="">
          <FaShoppingBasket />
        </LinkContainer>
      </LIElement>
      <LIElement>
        <LinkContainer to="">
          <FaSearch />
        </LinkContainer>
      </LIElement>
      <LIElement>
        <LinkContainer to="">
          <FaBars />
        </LinkContainer>
      </LIElement>
    </ULElement>
  );
};
