import { Link } from "react-router-dom";
import styled from "styled-components";
import {
  FaBars,
  FaSearch,
  FaShoppingBasket,
  FaUserCircle,
} from "react-icons/fa";
const color = "#0cc9c9";

export const ULElement = styled.ul`
  margin: 0;
  padding: 0;
  display: flex;
  list-style-type: none;
  position: relative;
`;

const SmallLIEmlemet = styled.li`
  display: inline-block;
  &:hover {
    background-color: red;
  }

  &::after {
    content: "";
    width: 0px;
    height: 1px;
    display: block;
    background: ${color};
    transition: 300ms;
  }
  &:hover::after {
    width: 100%;
  }
  padding: ${(props) => (props.padding ? "40px 0 10px 0 " : "10px 0")};
`;

const SmallContainer = styled.div`
  position: relative;
  background-color: blue;
  top: 50px;
  padding: 0 30px;
  width: 150px;
`;
const SmallULEmlemet = styled.ul`
  position: absolute;
  width: 100px;
  display: none;
  list-style-type: none;
  top: 0px;
  margin: 0;
  padding: 0;
`;

export const LIElement = styled.li`
  text-decoration: none;
  padding: ${(props) => (props.padding ? "0 100px" : "0 40px")};
`;

export const LinkContainer = styled(Link)`
  text-decoration: none;
  font-size: 16px;
  font-weight: bold;
  font-family: "Fira Sans", sans-serif;
  color: ${(props) => (props.primary ? color : "black")};
  display: inline-block;
  &:hover {
    color: ${color};
  }

  &:hover ${SmallULEmlemet} {
    transition: 300ms;
    display: block;
    opacity: 1;
    transform: translateY(0%);
    transition: transform 1s;
  }

  &::after {
    content: "";
    width: 0px;
    height: 1px;
    display: block;
    background: ${color};
    transition: 300ms;
  }
  &:hover::after {
    width: 100%;
  }
`;

export const Menubar = function () {
  return (
    <ULElement>
      <LIElement>
        <LinkContainer primary to="admin/user">
          Home
        </LinkContainer>
      </LIElement>
      <LIElement>
        <LinkContainer to="">Pages</LinkContainer>
      </LIElement>
      <LIElement>
        <LinkContainer to="">
          Cosmetic
          <SmallULEmlemet>
            <SmallContainer>
              <SmallLIEmlemet>BHA</SmallLIEmlemet>
              <SmallLIEmlemet>AHA</SmallLIEmlemet>
              <SmallLIEmlemet>Acne Treatment</SmallLIEmlemet>
              <SmallLIEmlemet>Sunscreen</SmallLIEmlemet>
            </SmallContainer>
          </SmallULEmlemet>
        </LinkContainer>
      </LIElement>
      <LIElement>
        <LinkContainer to="">Blog</LinkContainer>
      </LIElement>
      <LIElement>
        <LinkContainer to="">Shop</LinkContainer>
      </LIElement>
      <LIElement>
        <LinkContainer to="">Element</LinkContainer>
      </LIElement>
      <LIElement padding>
        <LinkContainer to="">
          <FaUserCircle />
        </LinkContainer>
      </LIElement>
      <LIElement>
        <LinkContainer to="">
          <FaShoppingBasket />
        </LinkContainer>
      </LIElement>
      <LIElement>
        <LinkContainer to="">
          <FaSearch />
        </LinkContainer>
      </LIElement>
      <LIElement>
        <LinkContainer to="">
          <FaBars />
        </LinkContainer>
      </LIElement>
    </ULElement>
  );
};
